import React, { Component } from 'react';
import CartProduct from './CartProduct';

// Takes props cartContent, totalCost, and active
class CartSection extends Component {
  render() {
    return (
      this.props.active && 
      <section className="cartSection" backgroundcolor="green">
        {this.state.cartContent.length > 0 ?
          this.state.cartContent.map((product, idx) => {
            //console.log(roll.type);
            return <CartProduct key={idx} roll={product} />
          })
          :
          <h3>The cart is empty!</h3>}
      </section>
    );
  }
}

export default CartSection;
