import React, { Component } from 'react';
import logo from '../logo-01.svg';
import {Glaze, Pack, glazingOptions, packSizeOptions, Product} from './Product.js';

class Roll {
  type;
  price;
  glazing;
  packSize;

  constructor(newType, newPrice, newGlazing, newPackSize){
    this.type     = newType;
    this.price    = newPrice;
    this.glazing  = newGlazing;
    this.packSize = newPackSize;
  }

  // Basic stringify function for debugging
  toString(){
    return "type: " + this.type + ", price: " + this.price + ", glazing: " 
            + this.glazing + ", packSize: " + this.packSize + "\n";
  }
}

// Navbar class to handle nav and future cart functionality
class Navbar extends Component {
  constructor(props) {
    super(props);
    this.state = {content: this.props.content,
                  priceTotal: 0.00};
  }

  componentDidUpdate(){
    console.log("Navbar updated - content is" + this.props.content);
  }

  render() {
    return(
      <header className="header">
        <img src={logo} alt="Bun Bun cinnamon roll logo" id="logo"/>
        <div className="headerText">
          <nav id="nav">
            <a href="." className="navText">PRODUCTS</a>
            <div id="cart">
              <a href="." className="navText" id="cartLink">CART</a>
              <div id="cartPopup" display={this.props.infoPopup}>
                <span className="addedSummaryText">Added to cart:</span><br/><br/>
						    <span className="addedSummaryText" id="addedProduct"></span><br/>
						    <span className="addedSummaryText" id="addedGlazing"></span><br/>
                <span className="addedSummaryText" id="addedPackSize"></span><br/>
                <span className="addedSummaryText" id="addedPrice"></span>
              </div>

              <div id="cartSummaryBox">
                <p className="cartSummary">{this.props.content + " item" + (2 > 1 ? "" : "s")}</p>
                <p className="cartSummary">{"Total: $ " + this.state.priceTotal}</p>
              </div>
            </div>
          </nav>

          <hr/>
          <h1 id="motto">Our hand-made cinnamon rolls</h1>
        </div>
      </header>
    );
  }
}

export {Navbar, Roll};
