import React, { Component } from 'react';
import Product from './Product.js';
import Navbar from './Navbar.js';
import {Roll} from './HelperStructs.js';

class Page extends Component{
  constructor(props){
    super(props);
    this.state = {cartContent: [],
                  listingContent: [new Roll(0, 0, 0, 0, 2.49), new Roll(1, 1, 0, 0, 3.49), 
                                    new Roll(2, 2, 0, 0, 2.99), new Roll(3, 3, 0, 0, 3.49),
                                    new Roll(4, 4, 0, 0, 3.99), new Roll(5, 5, 0, 0, 3.99)],
                  totalCost: 0.00};
  }

  // Method passed between components to update cart contents
  updateCart = (type, glazing, packSize, price) => {
    this.setState(prevState => ({
      cartContent: [...prevState.cartContent, new Roll(type, glazing, packSize, price)],
      totalCost: (parseFloat(prevState.totalCost) + parseFloat(price)).toFixed(2)}));
  }

  // Very short update functions, but having them keeps it clear where things are happening
  updatePack = (e) => {
    this.setState({packSize: e.currentTarget.value});
  }
  updateGlazing = (e) => {
    this.setState({glazing: e.currentTarget.value});
  }

  render(){
    return(
      <div className="Page">
        <Navbar content={this.state.cartContent} priceTotal={this.state.totalCost} infoPopup="none"/>

        <main className="catalog">
          {this.state.listingContent.map((roll, idx) => {
            console.log(roll.type);
            return <Product
              key       = {roll.id}
              id        = {idx} 
              roll      = {roll}
              glazeFunc = {this.updateGlazing} 
              packFunc  = {this.updatePack}
              cartFunc  = {this.updateCart} />
          })}
          {/*}
          <Product id={0} glazeFunc = {this.updateGlazing} packFunc = {this.updatePack}
                    cartFunc = {this.updateCart} roll = {this.state.listingContent[0]}/>

          <Product id={1} glazeFunc = {this.updateGlazing} packFunc = {this.updatePack} 
                    cartFunc = {this.updateCart}/>

          <Product id={2} glazeFunc = {this.updateGlazing} packFunc = {this.updatePack} 
                    cartFunc = {this.updateCart}/>

          <Product id={3} glazeFunc = {this.updateGlazing} packFunc = {this.updatePack} 
                    cartFunc = {this.updateCart}/>

          <Product id={4} glazeFunc = {this.updateGlazing} packFunc = {this.updatePack} 
                    cartFunc = {this.updateCart}/>

          <Product id={5} glazeFunc = {this.updateGlazing} packFunc = {this.updatePack} 
                    cartFunc = {this.updateCart}/>
          {*/}
        </main>
    </div>
  )}
}

export default Page;
