import React, { Component } from 'react';
import Product from './Product.js';
import {Navbar, Roll} from './Navbar.js';

class Page extends Component{
  constructor(props){
    super(props);
    this.state = {cartContent: [],
                  totalCost: 0.00};
  }

  updateCart = (type, glazing, packSize, price) => {
    this.setState(prevState => ({
      cartContent: [...prevState.cartContent, new Roll(type, glazing, packSize, price)],
      totalCost: (parseFloat(prevState.totalCost) + parseFloat(price)).toFixed(2)}));
    console.log("Cart updated: " + this.state.cartContent + ", total cost is $" + this.state.totalCost);
  }

  render(){
    return(
      <div className="Page">
        <Navbar content={this.state.cartContent} priceTotal={this.state.totalCost} infoPopup="none"/>

        <main className="catalog">
          <Product id={0} cartFunc = {this.updateCart} type={0} glazing={0} packSize={0} price={2.49}/>

          <Product id={1} cartFunc = {this.updateCart} type={1} glazing={0} packSize={0} price={3.49}/>

          <Product id={2} cartFunc = {this.updateCart} type={2} glazing={0} packSize={0} price={2.99}/>

          <Product id={3} cartFunc = {this.updateCart} type={3} glazing={0} packSize={0} price={3.49}/>

          <Product id={4} cartFunc = {this.updateCart} type={4} glazing={0} packSize={0} price={3.99}/>

          <Product id={5} cartFunc = {this.updateCart} type={5} glazing={0} packSize={0} price={3.99}/>
        </main>
    </div>
  )}
}

export default Page;
