import React, { Component } from 'react';
import logo from '../logo-01.svg';
import {Glaze, Pack, glazingOptions, packSizeOptions, Product} from './Product.js';

class Roll {
  type;
  price;
  glazing;
  packSize;

  constructor(newType, newPrice, newGlazing, newPackSize){
    this.type     = newType;
    this.price    = newPrice;
    this.glazing  = newGlazing;
    this.packSize = newPackSize;
  }

  /*
    Basic method to calculate the price of a roll rather than updating the roll's
    type price every time. This keeps me from needing to use magic numbers for
    creating new rolls.
  */
  getPrice(){
    return ((this.price + this.glazing.glazingPrice) * this.packSize.packPrice).toFixed(2);
  }

  // Basic stringify function for debugging
  toString(){
    return "type: " + this.type + ", price: " + this.price + ", glazing: " 
            + this.glazing + ", packSize: " + this.packSize + "\n";
  }
}

// Navbar class to handle nav and future cart functionality
class Navbar extends Component {
  constructor(props) {
    super(props);
    this.state = {content: this.props.cartContent,
                  priceTotal: 0.00};
  }

  render() {
    return(
      <header className="header">
        <img src={logo} alt="Bun Bun cinnamon roll logo" id="logo"/>
        <div className="headerText">
          <nav id="nav">
            <a href="." className="navText">PRODUCTS</a>
            <div id="cart">
              <a href="." className="navText" id="cartLink">CART</a>
            </div>
          </nav>

          <hr/>
          <h1 id="motto">Our hand-made cinnamon rolls</h1>
        </div>
      </header>
    );
  }
}

export default {Roll, Navbar};
